import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Inventory API
export const inventoryAPI = {
  getInventory: () => api.get('/inventory'),
  addInventory: (data) => api.post('/inventory', data),
  getInventoryHistory: () => api.get('/inventory/history')
};

// Customers API
export const customersAPI = {
  getCustomers: (type) => api.get('/customers', { params: { type } }),
  addCustomer: (data) => api.post('/customers', data),
  updateCustomer: (id, data) => api.put(`/customers/${id}`, data),
  deleteCustomer: (id) => api.delete(`/customers/${id}`),
};

// Orders API
export const ordersAPI = {
  getOrders: (status, customerId) => api.get('/orders', { 
    params: { status, customer_id: customerId } 
  }),
  createOrder: (data) => api.post('/orders', data),
  updateOrderStatus: (id, status) => api.put(`/orders/${id}/status`, { status }),
};

// Reports API
export const reportsAPI = {
  getSalesReport: (period) => api.get('/reports/sales', { params: { period } }),
  getInventoryReport: () => api.get('/reports/inventory'),
};

export default api;
